/**
 * Mark Down Component Component
 * which styles the HTML Elements generated by the "react-markdown" component
 */
import { PALETTE } from 'Constants/PALETTE';
import styledComponents from 'styled-components';

const MarkdownWrapper: React.FunctionComponent = styledComponents.div`
  font-family: Merienda, sans-serif;
  font-size: 0.85em;

  h1,
  h2,
  h3 {
    font-family: Rochester, sans-serif;
    color: ${PALETTE.DARK_CYAN};
    display: block;
    position: relative;
    z-index: 1;
    font-weight: normal;
    margin: 0;

    img {
      display: block;
      border: 4px solid lightgray;
      margin: -0.25em 0 0.25em 0.25em;
      z-index: -1;
      position: relative;
      max-width: 50vw;

      &.no-border {
        margin: 0.25em 0 0.25em 0.25em;
        border: none;
      }
    }
  }

  h1 {
    font-size: 2.9em;
    font-weight: normal;
    padding: 0;
    margin: -1em 0 0 0;
    text-align: right;
    float: right;
  }

  h2 {
    font-size: 2em;
  }

  h3 {
    font-size: 1.5em;
  }

  p {
    line-height: 1.75em;
  }

  .clear {
    clear: both;
  }

  .marginTop {
    margin-top: 3.5em;
  }

  > span {
    img:nth-of-type(1) {
      float: left;
      margin: 0.25em 1em 0.25em 0;
      max-width: 50vw;
      border: 4px solid lightgray;
    }
  }

  .right {
    float: right;
    padding-left: 1em;

    img:nth-of-type(1) {
      float: none;
      margin: -0.25em 0 0 0;
    }
  }

  a {
    color: ${PALETTE.DARK_RED};
    text-decoration: none;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, 0.25);

    &:hover {
      text-decoration: underline;
    }
  }

  ul {
    line-height: 2em;
    list-style-position: inside;
    padding-inline-start: 0.5em;
  }

  @media (min-width: 768px) {
    font-size: 0.8em;
  }

  @media (min-width: 1024px) {
    font-size: 1em;
  }
`;

export { MarkdownWrapper };
